<?phpif (isset($_POST['idCat']) && is_numeric($_POST['idCat'])) {    $SelectNews = Simple_DbApi::select_db($_GET['name_tamlates'], 'id', 'id_cat', $_POST['idCat'], '', '', '', '');    foreach ($SelectNews as $j => $nd) {        CMS::DeleteSearch($nd['id']);    }    $SelectCat = Simple_DbApi::select_db($_GET['name_tamlates'] . "_cat", '*', 'ids', $_POST['idCat'], '', '', '', '');    $nc = current($SelectCat);    Simple_DbApi::delete_db($_GET['name_tamlates'], 'id_cat', $_POST['idCat']);    CMS::UserEvents('<b>' . $_SESSION["login_admin"] . '</b> удалил категорию <b>' . $nc['names'] . '</b> в модуле <b>' . $TitleModule . '</b>. Раздел <a target="_blank" href="main-modules-setting-mod-' . $_GET['name_tamlates'] . '-3.pl"><b>Удалить категорию</b></a>');    Simple_DbApi::delete_db($_GET['name_tamlates'] . "_cat", "ids", $_POST['idCat']);    echo CMS::AlertWindow('Успешно', 'Категория удалена', 1, 0);}$SelectCatList = Simple_DbApi::select_db($_GET['name_tamlates'] . "_cat", "*", "", "", "names", 1, "", "");$list = '';if (!empty($SelectCatList)) {    foreach ($SelectCatList as $i => $nn) {        $list .= CMS::AdminModuleSection(63, '{id},{name},{window}', $nn['ids'] . '<><>' . $nn['names'] . '<><>' . CMS::AlertWindow('Внимание', CMS::AdminModuleSection(64, '{id}', $nn['ids']), 2, $nn['ids']));    }}echo CMS::AdminModuleSection(62, '{list}', $list);?>